import React from 'react';
import { useTour } from '@reactour/tour';
import { Popover } from '@reactour/popover';
import { useEffect } from 'react';

export default function Main() {
    const { setIsOpen } = useTour();

    useEffect(() => {
        setIsOpen(true);
    }, [setIsOpen]);

    return (
        <div className="demo scroll-demo">
            <Popover>
                <p>
                    <span className="first-step" style={{ backgroundColor: 'blue' }}>
                        Lorem ipsum
                    </span>{' '}
                    dolor sit amet, consectetur adipiscing elit. Praesent at finibus nulla, quis varius justo.
                </p>
                <p>
                    <span className="second-step">Vestibulum lorem</span> lorem.
                </p>
                <p>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque quis pretium odio. Sed hendrerit enim vel
                    libero sagittis efficitur. Maecenas iaculis metus et magna mollis, sit amet dictum arcu elementum. Vestibulum non
                    turpis at enim aliquet lobortis. Donec vel gravida tellus. Praesent nec tristique velit, at ullamcorper nibh.
                    Suspendisse potenti. Proin ac dolor justo. <span className="third-step">Bello bambo</span>, eleifend sed
                    iaculis a, tincidunt et tellus. Etiam vitae velit risus. Duis eget dolor mi.
                </p>
            </Popover>
        </div>
    );
}
